import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const PostList = () => {
  const [wpisy, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const deletePost = async (id) => {
    try {
      await axios.delete(`/api/posts/${id}`);
      fetchPosts();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Lista wpisów</h2>
      <Link to="/posts/add" className="btn btn-primary mb-3">Dodaj wpis</Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nazwa użytkownika</th>
            <th>Treść wpisu</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <tr key={post.id}>
              <td>{post.id}</td>
              <td>{post.name}</td>
              <td>{post.tresc}</td>
              <td>
                <Link to={`/posts/${post.id}`} className="btn btn-primary mr-2">Szczegóły</Link>
                <Link to={`/posts/${post.id}/edit`} className="btn btn-secondary mr-2">Edytuj</Link>
                <button className="btn btn-danger" onClick={() => deletePost(post.id)}>Usuń</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PostList;
