import { useEffect } from 'react';
import AuthUser from './AuthUser';

export default function Dashboard() {
  const { fetchUsers, fetchPosts, users, wpisy } = AuthUser();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    await fetchUsers();
    await fetchPosts();
  };

  return (
    <table className="table">
      <thead>
        <tr>
          <th>Nazwa użytkownika</th>
          <th>Treść wpisu</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user) => (
          wpisy
            .filter((wpis) => wpis.user_id === user.id)
            .map((wpis) => (
              <tr key={wpis.id}>
                <td>{user.name}</td>
                <td>{wpis.tresc}</td>
                <td>
                  <button className="btn btn-danger">Usuń</button>
                  <button className="btn btn-primary">Edytuj</button>
                </td>
              </tr>
            ))
        ))}
      </tbody>
    </table>
  );
}
