import React, { useEffect } from 'react';
import AuthUser from './AuthUser';

export default function Dashboard() {
  const {
    users,
    wpisy,
    fetchUsers,
    fetchPosts
  } = AuthUser();

  useEffect(() => {
    fetchPosts();
  }, []);

  useEffect(() => {
    fetchUsers(wpisy); // Dodane przekazywanie wpisów
  }, [wpisy]);

  return (
    <div>
      <h1>Dashboard</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Tresc</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>
                {wpisy
                  .filter(wpis => wpis.user_id === user.id)
                  .map(wpis => wpis.tresc)
                  .join(', ')}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
