import { useState, useEffect } from "react"
import AuthUser from './AuthUser';

export default function Login() {
    const {http,setToken} = AuthUser();
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState({});

    useEffect(() => {
        if (showModal) {
          const timer = setTimeout(() => {
            setShowModal(false);
          }, 5000);

          return () => clearTimeout(timer);
        }
      }, [showModal]);

    const submitForm = () => {
        // api call
        http.post('/login', { email: email, password: password })
          .then((res) => {
            setToken(res.data.user, res.data.access_token);
            setModalContent({
              title: "Sukces",
              message: "Logowanie udane!",
              color: "success"
            });
            setShowModal(true);
          })
          .catch(() => {
            setModalContent({
              title: "Błąd",
              message: "Nieprawidłowe dane logowania!",
              color: "danger"
            });
            setShowModal(true);
          });
      }

    const closeModal = () => {
    setShowModal(false);
    }

    return(
        <div className="row justify-content-center pt-5">
            <div className="col-sm-6">
                <div className="card p-4">
                    <h1 className="text-center mb-3">Zaloguj sie </h1>
                    <div className="form-group">
                        <label>Adres email:</label>
                        <input type="email" className="form-control" placeholder="Podaj email"
                            onChange={e=>setEmail(e.target.value)}
                        id="email" />
                    </div>
                    <div className="form-group mt-3">
                        <label>Haslo:</label>
                        <input type="password" className="form-control" placeholder="Podaj haslo"
                            onChange={e => setPassword(e.target.value)}
                        id="pwd" />
                    </div>
                    <button type="button" onClick={submitForm} className="btn btn-primary mt-4">Zaloguj sie</button>
                </div>
            </div>
            {showModal && (
        <div className="modal d-block" tabIndex="-1" role="dialog">
          <div className={`modal-dialog modal-dialog-centered modal-${modalContent.color}`} role="document">
            <div className="modal-content">
              <div className="modal-body">
                <h5 className="modal-title">{modalContent.title}</h5>
                <p>{modalContent.message}</p>
              </div>
            </div>
          </div>
        </div>
      )}
        </div>
    )
}
