import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function App() {
  const [users, setUsers] = useState([]);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchUsers();
    fetchPosts();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchPosts = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/users/${id}`);
      fetchUsers();
    } catch (error) {
      console.error(error);
    }
  };

  const deletePost = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/posts/${id}`);
      fetchPosts();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Lista użytkowników</h2>
      <Link to="/users/create" className="btn btn-primary mb-3">
        Dodaj użytkownika
      </Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nazwa użytkownika</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>
                <Link to={`/users/${user.id}`} className="btn btn-info btn-sm me-2">
                  Szczegóły
                </Link>
                <Link to={`/users/${user.id}/edit`} className="btn btn-warning btn-sm me-2">
                  Edytuj
                </Link>
                <button
                  onClick={() => deleteUser(user.id)}
                  className="btn btn-danger btn-sm"
                >
                  Usuń
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Lista wpisów</h2>
      <Link to="/posts/create" className="btn btn-primary mb-3">
        Dodaj wpis
      </Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Treść wpisu</th>
            <th>Autor</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <tr key={post.id}>
              <td>{post.id}</td>
              <td>{post.content}</td>
              <td>{post.user.name}</td>
              <td>
                <Link to={`/posts/${post.id}`} className="btn btn-info btn-sm me-2">
                  Szczegóły
                </Link>
                <Link to={`/posts/${post.id}/edit`} className="btn btn-warning btn-sm me-2">
                  Edytuj
                </Link>
                <button
                  onClick={() => deletePost(post.id)}
                  className="btn btn-danger btn-sm"
                >
                  Usuń
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
