import React, { useEffect} from 'react';
import AuthUser from './AuthUser';
import { Link,useNavigate } from 'react-router-dom';

export default function Home() {
  const { wpisy, fetchPosts } = AuthUser();

  useEffect(() => {
    fetchPosts();
  }, []);
  const navigate = useNavigate();
  const handleEdit = (id) => {
    navigate(`/edit/${id}`);
  };

  return (

    <div>
      <div class="container">
    <h1 class="mt-4 mb-4">Posty</h1>

    <div class="row">
      <div class="col-md-8 offset-md-2">
      <Link to="/create" className="btn btn-success">Dodaj post</Link>
      </div>
    </div>
    <div class="row">
      <div class="col-md-8 offset-md-2">
        {wpisy.map((wpis) => (
        <div class="card mb-4" key={wpis.id}>
          <div class="card-body">
            <h5 class="card-title">{wpis.id}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{wpis.user_id}</h6>
            <p class="card-text">{wpis.tresc}</p>
            <button className="btn btn-primary" nonClick={() => handleEdit(wpis.id)}>Edytuj</button>
            <a href="#" class="btn btn-danger">Usu≈Ñ</a>
            <a href="#" class="btn btn-info">Komentarze (0)</a>
          </div>
        </div>))}
      </div>
    </div>
  </div>
    </div>
  );
}
