import React, { useState } from 'react';
import axios from 'axios';

export default function Addwpis() {
  const [tytul, setTytul] = useState('');
  const [tresc, setTresc] = useState('');
  const [userId, setUserId] = useState('');

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    if (name === 'tytul') {
      setTytul(value);
    }
    if (name === 'tresc') {
      setTresc(value);
    }
    if (name === 'userId') {
      setUserId(value);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/api/wpisy', {
        tytul: tytul,
        tresc: tresc,
        user_id: userId,
      });

      console.log(response.data); // Możesz zastosować odpowiednie przekierowanie lub inne działania po dodaniu rekordu
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Dodaj Wpis</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tytuł:</label>
          <input
            type="text"
            name="tytul"
            value={tytul}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Treść:</label>
          <input
            type="text"
            name="tresc"
            value={tresc}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>ID użytkownika:</label>
          <input
            type="text"
            name="userId"
            value={userId}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Dodaj</button>
      </form>
    </div>
  );
}
