import React, { useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import AuthUser from './AuthUser';

export default function EditWpis({ match }) {
    const { id } = useParams();
  const { wpisy, fetchPosts, http, user } = AuthUser();

  const [tytul, setTytul] = useState('');
  const [wpis, setWpis] = useState({});
  const [tresc, setTresc] = useState('');
  const [userId, setUserId] = useState('');
  const [showSuccessAlert, setShowSuccessAlert] = useState(false);

  useEffect(() => {
    fetchPosts();
  }, []);

  useEffect(() => {
    const selectedWpis = wpisy.find((item) => item.id === parseInt(id));
    if (selectedWpis) {
      setWpis(selectedWpis);
      setTytul(selectedWpis.tytul);
      setTresc(selectedWpis.tresc);
      setUserId(selectedWpis.user_id);
    }
  }, [id, wpisy]);
  

  const handleUpdate = async () => {
    try {
      const response = await http.put(`/wpisy/${id}`, {
        tytul: tytul,
        tresc: tresc,
        user_id: user.id,
      });
      console.log('Wpis został zaktualizowany:', response.data);
      setShowSuccessAlert(true);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div class="container">
    <center><h1 class="mt-4 mb-4">Dodaj Wpis</h1></center>
    <div className="col-md-8 offset-md-2">
    {showSuccessAlert && (
                        <div className="alert alert-success" role="alert">
                        Dodano wpis!
                        </div>
                    )}
    <form onSubmit={handleSubmit}>
      <div class="form-group">
        <label for="tytul">Tytuł:</label>
        <input
          type="text"
          class="form-control"
          id="tytul"
          name="tytul"
          value={tytul}
          onChange={handleInputChange}
          required
        />
      </div>
      <div class="form-group">
        <label for="tresc">Treść:</label>
        <textarea
          class="form-control"
          id="tresc"
          name="tresc"
          rows="5"
          value={tresc}
          onChange={handleInputChange}
          required
        ></textarea>
      </div>
      <button type="submit" class="btn btn-primary btn-block">Dodaj</button>
    </form>
  </div>
  </div>
      <h1>Edytuj wpis</h1>
      {showSuccessAlert && (
        <div className="alert alert-success" role="alert">
        Wpis został zmieniony pomyślnie!
        </div>
      )}
      <div>
        <label htmlFor="tytul">Tytuł:</label>
        <textarea
          id="tytul"
          value={tytul}
          onChange={(e) => setTytul(e.target.value)}
        ></textarea>
      </div>
      <div>
        <label htmlFor="tresc">Treść:</label>
        <textarea
          id="tresc"
          value={tresc}
          onChange={(e) => setTresc(e.target.value)}
        ></textarea>
      </div>
      <div>
        <label htmlFor="userId">ID użytkownika:</label>
        <input
          type="number"
          id="userId"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
      </div>
      <button className="btn btn-primary" onClick={handleUpdate}>
        Zaktualizuj
      </button>
    </div>
  );
}
