import React, { useEffect, useState} from 'react';
import {toast} from 'react-toastify';
import AuthUser from './AuthUser';
import { Link, useNavigate } from 'react-router-dom';

export default function AddWpis() {
  const { wpisy, fetchPosts, fetchUsers, getUserNickById, http, fetchComments, komentarze } = AuthUser();
  const navigate = useNavigate();

  const notify = () => {
    toast.success('Pomyślnie usunięto wpis!', { autoClose: 5000 });
  };

  useEffect(() => {
    fetchPosts();
    fetchUsers();
  }, []);

  useEffect(() => {
    wpisy.forEach((wpis) => {
      fetchComments(wpis.id);
    });
  }, [wpisy]);

  const handleEdit = (id) => {
    navigate(`/edit/${id}`);
  };

  const handleDelete = async (wpisId) => {
    try {
      await http.delete(`/wpisy/${wpisId}`);
      fetchPosts();
      notify();
    } catch (error) {
      console.error(error);
    }
  };

  const getCommentCount = (wpisId) => {
    const wpisComments = komentarze.filter((komentarz) => komentarz.wpisy_id === wpisId);
    return wpisComments.length;
  };

  return (
    <div>
        <center>
          <h1 className="mt-4 mb-4">Posty</h1>
        </center>
        <div className="row">
          <div className="col-md-8 offset-md-2">
            <center>
              <Link to="/addwpis" className="btn btn-success">
                Dodaj post
              </Link>
            </center>
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-md-8 offset-md-2">
            {wpisy.sort((a, b) => b.id - a.id).map((wpis) => (
              <div className="card mb-4" key={wpis.id}>
                <div className="card-body">
                  <h5 className="card-title">{wpis.tytul} | ID:{wpis.id}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">
                  {getUserNickById(wpis.user_id)} | ID:{wpis.user_id}
                  </h6>
                  <p className="card-text">{wpis.tresc}</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleEdit(wpis.id)}
                  >
                    Edytuj
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(wpis.id)}
                  >
                    Usuń
                  </button>
                  <button
                    className="btn btn-info"
                    onClick={() => navigate(`/wpis/${wpis.id}`)}
                  >
                    Komentarze ({getCommentCount(wpis.id)})
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
    </div>
  );
}
